mapper.select:
select sys_powernum.powerInfoId, sys_powernum.powerNum01,
sys_powernum.powerNum02, sys_powernum.powerNum03,
sys_powernum.powerNum04, sys_powernum.powerNum05,
sys_powernum.powerNum06, sys_powernum.remark, sys_powernum.com_flag,
sys_powernum.create_by, sys_powernum.create_time,
sys_powernum.update_by, sys_powernum.update_time,
sys_powernum.company,sys_powernum.bill_date,sys_dept.dept_name,sys_dept.leader from sys_powernum
left join sys_dept on sys_powernum.company = sys_dept.dept_id

	 and powerInfoId = #{powerInfoId}
	 and powerNum01 = #{powerNum01}
	 and powerNum02 = #{powerNum02}
	 and powerNum03 = #{powerNum03}
	 and powerNum04 = #{powerNum04}
	 and powerNum05 = #{powerNum05}
	 and powerNum06 = #{powerNum06}
	 and remark = #{remark}
	 and com_flag = #{comFlag}
	 and create_by = #{createBy}
	 and create_time = #{createTime}
	 and update_by = #{updateBy}
	 and update_time = #{updateTime}
	 and company = #{company}
	 and bill_date = #{billDate}

select sys_powernum.powerInfoId, sys_powernum.powerNum01, sys_powernum.powerNum02, sys_powernum.powerNum03, sys_powernum.powerNum04, sys_powernum.powerNum05, sys_powernum.powerNum06, sys_powernum.remark, sys_powernum.com_flag, sys_powernum.create_by, sys_powernum.create_time, sys_powernum.update_by, sys_powernum.update_time, sys_powernum.company,sys_powernum.bill_date,sys_dept.dept_name,sys_dept.leader from sys_powernum left join sys_dept on sys_powernum.company = sys_dept.dept_id
where sys_powernum.powerInfoId in
#{powerInfoId}

mapper.update:
update sys_powernum
powerNum01 = #{powerNum01}, powerNum02 = #{powerNum02}, powerNum03 = #{powerNum03}, powerNum04 = #{powerNum04}, powerNum05 = #{powerNum05}, powerNum06 = #{powerNum06}, remark = #{remark}, com_flag = #{comFlag}, create_by = #{createBy}, create_time = #{createTime}, update_by = #{updateBy}, update_time = #{updateTime}, company = #{company}, bill_date = #{billDate},
where powerInfoId = #{powerInfoId}
